package com.example.final_proj1.fragments;

import android.content.Intent;
import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.denzcoskun.imageslider.constants.ScaleTypes;
import com.denzcoskun.imageslider.models.SlideModel;
import com.example.final_proj1.Adapter.Postadapter;
import com.example.final_proj1.Entity.Post;
import com.example.final_proj1.R;
import com.example.final_proj1.TopicsActivity;
import com.example.final_proj1.databinding.FragmentHomeBinding;

import java.util.ArrayList;

public class HomeFragment extends Fragment {
    FragmentHomeBinding fragmentHomeBinding;
    ArrayList<Post> postArrayList = new ArrayList<>();
    Postadapter postadapter = new Postadapter(postArrayList, getContext());
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {

        fragmentHomeBinding = FragmentHomeBinding.inflate(inflater,container,false);
        return fragmentHomeBinding.getRoot();

    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);

        fragmentHomeBinding.tbarIcgoto.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent intent = new Intent(getActivity(), TopicsActivity.class);
                startActivity(intent);
            }
        });

        postArrayList.add(new Post("1",2, 5,
                "ahmad",
                "وجبة غدا مناسبة للدايت \n- شيش منقوع بخل و توم و بهارات الي بتحبوها + معلقة زيت زيتون + بصلة + فطر + فلفل احمر + فلفل اخضر+ جزر",
                "22-5 ", "data:image/jpeg;base64,",
                "data:image/jpeg;base64,"));

        fragmentHomeBinding.rvPost.setAdapter(postadapter);
        fragmentHomeBinding.rvPost.setHasFixedSize(true);
        fragmentHomeBinding.rvPost.setLayoutManager(new LinearLayoutManager(getContext()));

        ArrayList<SlideModel> images = new ArrayList<>();
        images.add(new SlideModel(R.drawable.p1, ScaleTypes.CENTER_CROP));
        images.add(new SlideModel(R.drawable.p2, ScaleTypes.CENTER_CROP));
        images.add(new SlideModel("https://thumbs.dreamstime.com/b/jour-de-terre-d-environnement-dans-les-mains-des-arbres-cultivant-jeunes-plantes-bokeh-verdissent-la-main-femelle-fond-tenant-l-130247647.jpg", "in url", ScaleTypes.CENTER_CROP));
        images.add(new SlideModel(R.drawable.p3, ScaleTypes.CENTER_INSIDE));
        images.add(new SlideModel(R.drawable.p4, ScaleTypes.CENTER_INSIDE));
        images.add(new SlideModel("https://cdn.pixabay.com/photo/2015/04/23/22/00/tree-736885__480.jpg", "in url", ScaleTypes.CENTER_CROP));
        images.add(new SlideModel(R.drawable.p5, ScaleTypes.CENTER_CROP));
        images.add(new SlideModel(R.drawable.p6, ScaleTypes.CENTER_CROP));
        images.add(new SlideModel(R.drawable.p7, ScaleTypes.CENTER_CROP));
        fragmentHomeBinding.sliderImage.setImageList(images);
    }
}